// 1. 求两个数的最大值
// 2. 求数组的最大值
#include <iostream>
using namespace std;

int getMax(int a, int b){
	return a > b ? a : b;
}
/**
 * 函数功能：返回数组中的最大值
 * @param  arr   整型数组
 * @param  count 数组长度
 * @return       数组的最大值
 */
int getMax(int *arr, int count){
	int maxNum = arr[0];
	for(int i =1; i < count; i++){
		if (maxNum < arr[i]){
			maxNum = arr[i];
		}
	}
	return maxNum;
}

int main(void){
