	void printX(){
		cout << x << endl;
	}
	void printY(){
		cout << y << endl;
	}
};

int main(void){
	// 栈
	Coordinate Coor;
	Coor.x = 10;
	Coor.y = 20;
	Coor.printX();
	Coor.printY();

	// 堆
	Coordinate *p = new Coordinate();  // 申请内存失败，返回NULL
	if (NULL == p){
		// failed 申请内存失败，直接返回
		return 0;
	}
	p->x = 100;
	p->y = 200;
	p->printX();
	p->printY();
	// 清空内存
	delete p;
	p = NULL;

	system("pause");
	return 0;
}
